import React from 'react';

class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      message: '', // Повідомлення (успіх або помилка)
    };
    this.sendMessage = this.sendMessage.bind(this);
  }

  sendMessage(event) {
    if (event) event.preventDefault();

    // Отримуємо значення полів вводу
    const name = document.getElementById('name').value.trim();
    const email = document.getElementById('email').value.trim();
    const message = document.getElementById('message').value.trim();

    // Перевіряємо, чи всі поля заповнені
    if (name === '' || email === '' || message === '') {
      this.setState({ message: 'Помилка в заповненні' }); // Встановлюємо помилку
    } else {
      this.setState({ message: 'Message sent!' }); // Встановлюємо успішне повідомлення

      // Очищуємо поля
      document.getElementById('name').value = '';
      document.getElementById('email').value = '';
      document.getElementById('message').value = '';
    }

    // Автоматично ховаємо повідомлення через 3 секунди
    setTimeout(() => {
      this.setState({ message: '' });
    }, 3000);
  }

  render() {
    return (
      <div className="contact">
        {/* Блок повідомлення в правому нижньому куті */}
        {this.state.message && (
          <div className={`message-box ${this.state.message === 'Помилка в заповненні' ? 'error' : 'success'}`}>
            {this.state.message}
          </div>
        )}

        <div className="frame-3">
          <h2>Contact</h2>
          <div className="rectangle-151"></div>
          <p className="contact-text">
            <span className="first-line">Consectetur adipiscing elit viverra tristique placerat in massa consectetur </span>
            <span className="second-line">Vitae faucibus diam consequat maecenas. Turpis metus sit diam purus leo in varius ac quam lorem est persetera</span>
            <span className="third-line">
              <span className="email-website"><br /><b>EMAIL:</b></span> company@youremail.com
              <span className="email-website"><br /><b>WEBSITE:</b></span>www.yourwebsite.com
            </span>
          </p>
        </div>
        <div className="contact-form">
          <div className="input-container">
            <input type="text" id="name" className="input-field" placeholder="name" required />
            <label htmlFor="name" className="input-label">name</label>
          </div>
          <div className="input-container">
            <input type="email" id="email" className="input-field" placeholder="email" required />
            <label htmlFor="email" className="input-label">email</label>
          </div>
          <div className="input-container">
            <input id="message" className="input-field message" placeholder="message" required />
            <label htmlFor="message" className="input-label message">message</label>
          </div>
          <button type="submit" onClick={this.sendMessage} className="submit-button">SEND MESSAGE</button>
        </div>

        {/* CSS стилі для повідомлення */}
        <style>
          {`
            .message-box {
              position: fixed;
              bottom: 20px;
              right: 20px;
              padding: 10px 20px;
              border-radius: 5px;
              box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
              z-index: 1000;
              transition: opacity 0.5s ease-in-out;
              font-weight: bold;
            }

            .success {
              background-color: #4caf50;
              color: white;
            }

            .error {
              background-color: #ff4d4d;
              color: white;
            }
          `}
        </style>
      </div>
    );
  }
}

export default Contact;
