import React, { useState } from "react";

const Menu = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [activeSection, setActiveSection] = useState(null); // Стан для активного пункту

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: "smooth" });
      setActiveSection(id); // Змінюємо активний пункт
      setIsOpen(false); // Закриваємо меню
    }
  };

  return (
    <div className={`menu ${isOpen ? "open" : ""}`}>
      <div className="menu-icon" onClick={toggleMenu}>
        <div className="line line1"></div>
        <div className="line line2"></div>
        <div className="line line3"></div>
      </div>

      <div className="social-links">
        <a
          onClick={() => scrollToSection("it-story")}
          className={activeSection === "it-story" ? "active" : ""}
        >
          Моя історія в ІТ
        </a>
        <a
          onClick={() => scrollToSection("hobbies")}
          className={activeSection === "hobbies" ? "active" : ""}
        >
          Мої хобі
        </a>
        <a
          onClick={() => scrollToSection("family")}
          className={activeSection === "family" ? "active" : ""}
        >
          Про мою сімʼю
        </a>
        <a
          onClick={() => scrollToSection("about-me")}
          className={activeSection === "about-me" ? "active" : ""}
        >
          Про мене
        </a>
      </div>
    </div>
  );
};

export default Menu;
