const Menu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: "smooth" });
      setIsOpen(false); // Закриваємо меню після натискання
    }
  };

  return (
    <div className={`menu ${isOpen ? 'open' : ''}`}>
      <div className="menu-icon" onClick={toggleMenu}>
        <div className="line line1"></div>
        <div className="line line2"></div>
        <div className="line line3"></div>
      </div>

      <div className="social-links">
        <a onClick={() => scrollToSection("it-story")}>Моя історія в ІТ</a>
        <a onClick={() => scrollToSection("hobbies")}>Мої хобі</a>
        <a onClick={() => scrollToSection("family")}>Про мою сімʼю</a>
        <a onClick={() => scrollToSection("about-me")}>Про мене</a>
      </div>
    </div>
  );
};

export default Menu;
